import React from 'react'
import { useState } from 'react'

function ToggleProject() {

  const projectType = 'Back-End'
  const amount = 2


  const [isToggle,setIsToggle] = useState(false)

  const handleClick = () => {
    if(isToggle){
      setIsToggle(false)
    }else{
      setIsToggle(true)
    }
  }




  return (
    <div className='toggle-project' onClick={() => handleClick}>
      {projectType} 
      {isToggle && 
        <div>
        work
        </div>
      }
    </div>
  )
}

export default ToggleProject
