import React from 'react'
import { useState } from 'react'

function ToggleProject({projects}) {


  const projectType = 'Back-End'
  const amount = 2


  const [isToggle,setIsToggle] = useState(false)

  const handleClick = () => {
    if(isToggle){
      setIsToggle(false)
      console.log('false')
    }else{
      setIsToggle(true)
      console.log('true')
    }
  }




  return (
    <div className='flex-toggle'>
      <div className='toggle-project' onClick={() => handleClick()}>
        {projectType} 
       
      </div>
      {isToggle && 
        <div>
          <img/>
          <span>ifood</span>
        </div>
        }
    </div>
    
  )
}

export default ToggleProject
