import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import {useHistory,useEffect} from 'react-router-dom';
import { projectData } from './ProjectData';
import Projects from './Projects/Projects';
import Home from './Home/Home';
import NavBar from './NavBar/navBar';
import ContactMe from './Home/ContactMe/ContactMe';


function App() {
  const history = useHistory();

  useEffect(() => {
    const unlisten = history.listen(() => {
      // Scroll to the top of the page when a new link is clicked
      window.scrollTo(0, 0);
    });
    return () => {
      unlisten();
    };
  }, [history]);


  return (
    <>
    <NavBar/>
    <Routes>
      <Route exact path='/' element = {
          <Home/>
      }/>
      <Route path = "/backEndProjectFoodDelivery" element={
          <Projects
           projectData = {projectData[0]}
          />
      }/> 

      <Route path = "/frontEndProjectArchitectPortifolio" element={
          <Projects
            projectData = {projectData[1]}

          />
      }/> 
      <Route path = "/backEndProjectWebStore" element={
          <Projects
           projectData = {projectData[2]}
          />
      }/> 
    </Routes>
    <ContactMe/>



    </>
  );
}

export default App;
