import React, { useState } from 'react';
import './classes.css';
import userClass from './userClass.svg'

function Classes({ informationPage }) {
  const [isListVisible, setListVisible] = useState(
    Array(informationPage.classes.length).fill(false)
  );

  const toggleList = (index) => {
    const updateList = [...isListVisible];
    updateList[index] = !updateList[index];
    setListVisible(updateList);
  };

  return (
    <div className='labels'>
      <img src={userClass}/>
      {informationPage.classes.map((item, index) => (
        <React.Fragment key={index}>
          <label onClick={() => toggleList(index)}>{item}</label>
          {isListVisible[index] && (
            <div className='method-table'>
              <table>
                <tbody>
                  <tr>
                    <th>Method</th>
                    <th>URl</th>
                    <th>Description</th>
                  </tr>
                  {informationPage.methods[index].map((object, subIndex) => (
                    <tr key={subIndex}>
                      <td>{object.method}</td>
                      <td>{object.URL}</td>
                      <td>{object.description}</td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </React.Fragment>
      ))}
    </div>
  );
}

export default Classes;
