import React, { useEffect, useState } from 'react'


function ToggleButton({title,setActiveProject}) {

  const [active,setActive] = useState()

  useEffect(() => {
        
    const checkingActive = () => {
      if(title === 'Back-End'){
        setActive(true)
      }else{
        setActive(false)
      }
    }

    (() => checkingActive)()
  },[])


  const handleClick = () => {
    if(title === 'Back-End'){
      setActiveProject(0)
    }else{
      setActiveProject(1)
    }
    setActive(!active)
  }


  return (
    <div className={`toggle-project-title ${active ? 'active' : ''}`} onClick={handleClick}>
        {title}
    </div>
  )
}

export default ToggleButton
