import React from 'react'


function ToggleButton({title,setActiveProject,active}) {



  const handleClick = () => {
    if(title === 'Back-End'){
      setActiveProject(0)
    }else{
      setActiveProject(1)
    }
    active != active
  }


  return (
    <div className={`toggle-project-title ${active ? 'active' : ''}`} onClick={handleClick}>
        {title}
    </div>
  )
}

export default ToggleButton
