import React, { useEffect, useState } from 'react'
import { projectData } from '../ProjectData'


function ToggleButton({title,activeProject,setActiveProject}) {


  const [active,setActive] = useState()

  useEffect(() => {
        
    const checkingActive = () => {
      if(title === 'Back-End'){
        setActive(true)
      }else{
        setActive(false)
      }
    }

    (() => checkingActive())()
  },[])


  const handleClick = () => {
    const initActive = activeProject
   
    if(title === 'Back-End'){
      setActiveProject(0)
    }else{
      setActiveProject(1)
    }

    if(activeProject !== initActive){
      setActive(!active)
    }
    
    
    
  }


  return (
    <div className={`toggle-project-title ${active ? 'active' : ''}`} onClick={handleClick}>
        {title}
    </div>
  )
}

export default ToggleButton
