import React, { useEffect, useState } from 'react'


function ToggleButton({title,activeProject,setActiveProject}) {


  const [active,setActive] = useState()
  const initActiveProject = activeProject

  useEffect(() => {
        
    const checkingActive = () => {
      if(title === 'Back-End'){
        setActive(true)
      }else{
        setActive(false)
      }
    }

    (() => checkingActive())()
  },[])


  const handleClick = () => {
    console.log(initActiveProject)
    console.log(activeProject)
   
    if(title === 'Back-End'){
      setActiveProject(0)
    }else{
      setActiveProject(1)
    }
    console.log(initActiveProject)
    console.log(activeProject)
    if(activeProject != initActiveProject){
      setActive(!active)
    }
    
  }


  return (
    <div className={`toggle-project-title ${active ? 'active' : ''}`} onClick={handleClick}>
        {title}
    </div>
  )
}

export default ToggleButton
