import React from 'react'
import { useState,useEffect } from 'react'
import { NavLink } from 'react-router-dom'
import './page1.css'
import { projectData } from '../../ProjectData'
import rigthArrow from '../../assets/images/icon/right-arrow.png'
import leftArrow from '../../assets/images/icon/left-arrow.png'

function Page1() {

  const backEndProjects = projectData.filter((item) => item.type === 1)

  const [type,setType] = useState(0)
  const [projectName,setProjectName] = useState('Full Stack Projects')
  const [activeProject,setActiveProject] = useState(backEndProjects)
  

  useEffect(() => {
    if(type === 0){
      setProjectName("Full Stack Projects")
  
    }else if(type === 1){
      setProjectName("Front-End Projects")
    }else if(type === 2) {
      setProjectName("Back-end Projects")
    }
  },[type])

  

  const changeNext = () => {

    if(type === 2){
      setType(0)
    }else{
      setType(type + 1)
    }
  }
  
  const changePrevious = () => {

    if(type === 0){
      setType(2)
    }else{
      setType(type - 1)
    }
  }


  return (
    <div className='page1'>
      <div className='project-type'>
        <img src={leftArrow} className='arrow-img' onClick={changePrevious} />
        <h1 className='page1-title'>{projectName}</h1>
        <img src={rigthArrow} className='arrow-img' onClick={changeNext}/>

      </div>
      <img className='projects-image' src={projectData[0].image[0]}/>
      
       <div className='projects-informations'>

        <h2 className='code'>{projectData[0].title}</h2>
        <h3 className='code'>{projectData[0].description}</h3>
        
        <NavLink to="/projectPage">
          <button className='project-button'>View More</button>
        </NavLink>
        


       </div>
    </div>
  )
}

export default Page1
