import React from 'react'
import { useState } from 'react';
import './classes.css'

function Classes({informationPage}) {

    const [isListVisible, setListVisible] = useState(false);

    const toggleList = () => {
      setListVisible(!isListVisible);
    };
  
  return (
    <div className='labels'>
        {informationPage.classes.map((item) => 
            
            <>
                <label onClick={toggleList}>
                    {item}
                </label>
                {isListVisible && (
                    <table>
                        <tr>
                            <th>Method</th>
                            <th>URl</th>
                            <th>description</th>
                        </tr>
                        {informationPage.methods[item.index].map((object) =>
                            <tr>
                                <td>{object.method}</td>
                                <td>{object.URL}</td>
                                <td>{object.description}</td>
                            </tr>    
                        )}
                    </table>
                   
                )}   
            </>
            
        )} 
        
    </div>
  )
}

export default Classes
