import React from 'react'
import { useState,useEffect } from 'react'
import './page1.css'
import { projectData } from '../ProjectData'
import rigthArrow from '../assets/images/icon/right-arrow.png'
import leftArrow from '../assets/images/icon/left-arrow.png'

function Page1() {

  const [type,setType] = useState(0)
  const [project_name,setProjectName] = useState('Full Stack Projects')

  useEffect(() => {
    if(type === 0){
      setProjectName("Full Stack Projects")
  
    }else if(type === 1){
      setProjectName("Front-End Projects")
    }else if(type === 2) {
      setProjectName("Back-end Projects")
    }
  },[type])

  

  const changeNext = () => {
    console.log("test")
    if(type === 2){
      setType(0)
    }else{
      setType(type + 1)
    }
  }
  
  const changePrevious = () => {
    if(type === 0){
      setType(2)
    }else{
      setType(type - 1)
    }
  }


  return (
    <div className='page1'>
      <div className='project-type'>
        <button onClick={() => changePrevious}><img src={leftArrow} className='arrow-img' /></button>
        <h1 className='page1-title'>{project_name}</h1>
        <img src={rigthArrow} className='arrow-img' onClick={() => changeNext}/>

      </div>

       <div>
        <img className='projects-image' src={projectData[0].image}/>
       </div>
    </div>
  )
}

export default Page1
