import React from 'react'
import { useState } from 'react'
import './projectpage.css'
import gitHub from '../assets/images/icon/programs icon/github-icon.png'
import smallRightArrow from '../assets/images/icon/small-right-arrow.png'
import smallLeftArrow from '../assets/images/icon/small-left-arrow.png'

function ProjectPage({projectData}) {
  const [isListVisible, setListVisible] = useState(false);

  const [ativeImage,setAtiveImage] = useState(0)
  const maxImage = projectData[0].image.length - 1

  const changeToNext = () => {
    if(ativeImage == maxImage){
      setAtiveImage(0)
    }else{
      setAtiveImage(ativeImage + 1)
    }
    
  }

  const changeToPrevious = () => {
    if(ativeImage == 0){
      setAtiveImage(maxImage)
    }else{
      setAtiveImage(ativeImage - 1)
    }
  }

  const toggleList = () => {
    setListVisible(!isListVisible);
  };

  return (
    <div className='project-page'>
          <div className='project-title'>
            <a href={projectData[0].gitLink} target='_blank'>
              <img src={gitHub}/>
            </a>            
            <h2 >{projectData[0].title}</h2>
          </div>

          <div className='div-image'>
            <img src={smallLeftArrow} className='small-arrow'/>
            <img src={projectData[0].image[ativeImage]} className='project-image' />
            <img src={smallRightArrow} className='small-arrow' onClick={changeToNext}/>
          </div>
         

          <div className='project-info'>
            <p>{projectData[0].description}</p> 
            <label htmlFor="toggle" className="toggle-label" onClick={toggleList}>
              Toggle List
            </label>
            <input type="checkbox" id="toggle" className="toggle-checkbox" />
            {isListVisible && (
              <ul className="toggle-list">
                <li>Item 1</li>
                <li>Item 2</li>
                <li>Item 3</li>
                <li>Item 4</li>
              </ul>
            )}
          </div>        
        

       
    </div>
  )
}

export default ProjectPage
