import React, { useEffect, useState } from 'react'
import { projectData } from '../ProjectData'


function ToggleButton({title,activeProject,setActiveProject}) {


  const [active,setActive] = useState()

  useEffect(() => {
        
    const checkingActive = () => {
      if(title === 'Back-End'){
        setActive(true)
      }else{
        setActive(false)
      }
    }

    (() => checkingActive())()
  },[])


  useEffect(() => {
    setActive(activeProject === (title === 'Back-End' ? 0 : 1));
  }, [activeProject, title]);

  const handleClick = () => {
    setActiveProject(title === 'Back-End' ? 0 : 1);
  }


  return (
    <div className={`toggle-project-title ${active ? 'active' : ''}`} onClick={handleClick}>
        {title}
    </div>
  )
}

export default ToggleButton
