import React from 'react'
import { useState } from 'react'
import gitHub from '../assets/images/icon/programs icon/github-icon.png'

function ToggleProject({projectType,projects}) {

  const amount = 2


  const [isToggle,setIsToggle] = useState(false)

  const handleClick = () => {
    if(isToggle){
      setIsToggle(false)
      
    }else{
      setIsToggle(true)
     
    }
  }




  return (
    <div className='flex-toggle'>
      <div className='toggle-project-title' onClick={() => handleClick()}>
        {projectType} 
       
      </div>
      {isToggle && 
        projects.map((item) => 
          <div className='toggle-project'>
            <img src={item.iconImage}/>
            <span>{item.title}</span>
            <img src={gitHub}/>
          </div>
        )
       
        }
    </div>
    
  )
}

export default ToggleProject
