import React from 'react'
import { useState } from 'react'
import './classes.css';
import ProjectArrowRigth from '../../../assets/images/icon/project-right-arrow.png'
import ProjectArrowLeft from '../../../assets/images/icon/project-left-arrow.png'

function CarousselImages({images}) {

    const [index,setIndex] = useState(0)

    const nextProject = () => {
       if(index + 1 >  images.length ){
            setIndex(0)
       }else{
            setIndex(index += 1)
            
       }
    }

    const previousProject = () => {
        if(index - 1 !== 0){
            setIndex(index - 1)
        }
    }


  return (
    <div className='caroussel-images'>
        <img src={ProjectArrowLeft} className='project-arrow' onClick={() => nextProject}/>
        <img src={images[index]} className='img-placeholder'/>
        <img src={ProjectArrowRigth} className='project-arrow' onClick={() => previousProject}/>

    </div>
  )
}

export default CarousselImages
